const FEMTO_INPUTS=document.querySelectorAll(".femto-input"),GENERATOR=document.getElementsByClassName("femto-generator")[0],NAME=GENERATOR.getElementsByClassName("femto-name")[0],DOWNLOAD=document.getElementsByClassName("femto-link")[0];function download(){html2canvas(GENERATOR).then(canvas=>{let link=document.createElement("a");link.download=NAME.innerHTML+".png",link.href=canvas.toDataURL(),link.click()})}FEMTO_INPUTS.forEach(input=>{input.addEventListener("keyup",()=>{NAME.innerHTML=input.value})}),DOWNLOAD.addEventListener("click",()=>{download()});