function download(){html2canvas(GENERATOR,{allowTaint:!0}).then(e=>{let t=document.createElement("a");t.download=LOGO_NAME+".png",t.href=e.toDataURL(),t.click()})}const FEMTO_INPUTS=document.querySelectorAll(".femto-input"),GENERATOR=document.getElementsByClassName("femto-generator")[0],NAME=GENERATOR.getElementsByClassName("femto-name")[0],DOWNLOAD=document.getElementsByClassName("femto-link")[0];let DF_WEIGHT=6,DF_IMAGE=128,LOGO_NAME="";FEMTO_INPUTS.forEach(e=>{e.addEventListener("keyup",()=>{LOGO_NAME=e.value,NAME.innerHTML="",NAME.style.fontSize=DF_WEIGHT-e.value.split("").length/4+"rem";const t=e.value.split("");t.forEach(t=>{const n=document.createElement("span");if(n.classList.add("femto-letter"),"o"===t.toLowerCase()){const t=document.createElement("img");t.src="assets/img/femto.png",t.alt="",t.crossOrigin="anonymous",t.style.transform="scaleX("+[1,-1][Math.floor(2*Math.random())]+")",n.appendChild(t),t.style.width=DF_IMAGE-5*e.value.split("").length+"px"}else n.innerHTML=t;NAME.appendChild(n)})})}),DOWNLOAD.addEventListener("click",()=>{download()});